import React from 'react'
// import GithubListUser from './GithubListUser';
// import GithubUser from './GithubUser'

function fetchGithub(query) {
    const URL = `https://api.github.com/search/users?q=${query}`
    return fetch(URL).then((response) => response.json())
}

export default class GithubList extends React.Component {
    state = {
        user: [], 
        isLoading: true, 
        searchUser: ' ',
    }


handleSubmit = (e) => {
    const searchTerm = e.target.value;
    this.setState({ 
        searchUser: searchTerm });
 fetchGithub(searchTerm).then((data) => {
     const users = data.items.map((user) => user.login); 
     console.log(users);
     this.setState({
         user: users, 
         isLoading: true
     })
 }).finally(() => this.setState({ isLoading: false}))
}

    render() {
        const { isLoading, user, searchUser } = this.state
        return (
            <div>
                <form onSubmit>
                    <input name="searchUser" value={searchUser} onChange={this.handleSubmit} />
                    <input type="submit" />                    
                </form>
                {isLoading ? 
                <h1>Loading... </h1> 
            : 
            user.map((e) => <li>{e}</li>)}
            </div>
        )
    }
}
