import React from 'react'


function fetchGithub(query) {
    const URL = `https://api.github.com/search/users?q=${query}`
    return fetch(URL).then((response) => response.json())
}

export default class GithubList extends React.Component {
    state = {
        users: [], 
        isLoading: true, 
        searchUser: ' ',
    }


handleSubmit = (e) => {
    const searchTerm = e.target.value;
    this.setState({ 
        searchUser: searchTerm });

        if(searchTerm.length )
 fetchGithub(searchTerm).then((data) => {
     const users = data.items.map((user) => user); 
     console.log(users);
     this.setState({
         users: users, 
         isLoading: true
     })
 }).finally(() => this.setState({ isLoading: false}))
}

    render() {
        const { isLoading, users, searchUser } = this.state
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input name="searchUser" value={searchUser} onChange={this.handleSubmit} />
                    <input type="submit" />                    
                </form>
                {isLoading ? 
                <h1>Loading... </h1> 
            : 
            <ul>
            {users.map((user) => <li key={user.id}>{user.login}</li>)}
            </ul>}
            </div>
        )
    }
}
