import React from 'react'

function fetchGithub(query) {
    const URL = `https://api.github.com/search/users?q=${query}`
    return fetch(URL).then((response) => response.json())
}
console.log(fetchGithub(Lucha2040));


export default class GithubList extends React.Component {
    state = {
        user: [ ], 
        isLoading: true, 
        searchUser: ' ',
    }

componentDidMount() {
    this.fetchUser();
}

fetchUser() {
    const query = this.state.searchUser 
    fetchGithub(query).then((body) => {
        this.setState({user: body.data, isLoading: false})
    })
}

changeSearchUser = (e) => {
    this.setState({ searchUser: e.target.value})
}

handleSubmit = (e) => {
    e.preventDefault(); 
    this.setState({ isLoading:true })
    this.fetchUser()
}

    render() {
        const { isLoading, user, searchUser } = this.state
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input name="searchUser" value={searchUser} onChange={this.changeSearchUser} />
                    <input type="submit" />
                </form>
                {isLoading ? 
                <h1>Loading... </h1> 
            : 
            <h2>GithubUser</h2>}
            </div>
        )
    }
}
