import React from 'react'
import GithubListUser from './GithubListUser';

function fetchGithub(query) {
    const URL = `https://api.github.com/search/users?q=${query}`
    return fetch(URL).then((response) => response.json())
}

export default class GithubList extends React.Component {
    state = {
        user: [], 
        isLoading: true, 
        searchUser: ' ',
    }

// changeSearchUser = (e) => {
//     this.setState({ searchUser: e.target.value})
// }

handleSubmit = (e) => {
    e.preventDefault(); 
    const searchTerm = e.target.value;
    this.setState({ 
        searchUser: searchTerm,
        isLoading:true })
}


fetchUser = () => {
    const query = this.state.searchUser 
    fetchGithub(query).then((user) => {
        this.setState({user: user, isLoading: false})
    })
}

    render() {
        const { isLoading, user, searchUser } = this.state
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input name="searchUser" value={searchUser} onChange={this.changeSearchUser} />
                    <input type="submit" onChange={this.fetchUser} />                    
                </form>
                {isLoading ? 
                <h1>Loading... </h1> 
            : 
            <GithubListUser user={user}/>}
            </div>
        )
    }
}
